# -*- coding: utf-8 -*-

def get_procedure_names(path):
    procedure_names = set()
    with open(path, encoding='utf-8') as fd:
        for line in fd.readlines():
            line = line.replace('\n', '').replace('\r', '').replace('_', '.').lower().strip()
            if line and not line.startswith('#'):
                procedure_names.add(line)
    return procedure_names


def as_basename(procedure_name):
    return procedure_name.replace('.', '_').lower()
