# -*- coding: utf-8 -*-
import polib


class MockSourceBackend(object):
    def __init__(self, keys_lang=False):
        self.data = {}
        self.keys_lang = keys_lang

    def languages(self):
        return ['en']

    def add_translation(self, msgid, msgstr):
        self.data[msgid] = msgstr

    def get_global_po(self, lang):
        po = polib.POFile()
        for msgid, msgstr in self.data.items():
            po.append(polib.POEntry(msgid=msgid, msgstr=msgstr))
        return po

    get_force_po = get_global_po
