# -*- coding: utf-8 -*-
import os
import unittest

from delia_mlg.backends import GtTranslateBackend, SanityBackend


class TestGtTranslateBackend(unittest.TestCase):
    def setUp(self):
        super(TestGtTranslateBackend, self).setUp()
        self.backend = GtTranslateBackend(SanityBackend())
        self.project_path = os.path.join(os.path.dirname(os.path.abspath(__file__)), 'gp')

    def test_parse(self):
        self.assertEquals({'Hello World'}, self.backend.parse(self.project_path, 'test'))

    def test_parse_not_exists(self):
        self.assertEquals(set(), self.backend.parse(self.project_path, 'not.exists'))


if __name__ == '__main__':
    unittest.main()
