# -*- coding: utf-8 -*-
import unittest

from delia_mlg.backends import RedisBackend


class TestRedisBackend(unittest.TestCase):
    def setUp(self):
        super(TestRedisBackend, self).setUp()
        self.backend = RedisBackend('redis://chardon:6379/2')

    def test_get(self):
        self.assertEqual('Rate name ', self.backend.get('Nom de taux ', 'en'))
        self.assertIsNone(self.backend.get('foo bar truc ', 'en'))


if __name__ == '__main__':
    unittest.main()
