# -*- coding: utf-8 -*-
import os
import tempfile
import unittest

from delia_mlg import tools


class TestTools(unittest.TestCase):
    def test_get_procedure_names(self):
        with tempfile.TemporaryDirectory() as tmp:
            path = os.path.join(tmp, 'procedures.cfg')
            with open(path, 'w', encoding='utf-8') as fd:
                fd.write('''
toto
    tata_TUTU
# tutu


''')
            self.assertEqual({'toto', 'tata.tutu'}, tools.get_procedure_names(path))


if __name__ == '__main__':
    unittest.main()
