#-*- coding: utf-8 -*-
import warnings
from delia_preprocessor.macro import scan_macro
from delia_preprocessor.include import scan_include
from delia_preprocessor.iterator import fill, filter


def scanne_all(scanner):
    '''Old scan method: deprecated'''
    files = []
    acros = {}
    warnings.warn('scanne_all deprecated')
    return files, acros, filter(scan_macro(scan_include(fill(scanner), scanner.path, files=files, stack=[], include=None), acros=acros, stack=[]))


def scanne_include(scanner):
    '''Old scan method: deprecated'''
    files = []
    warnings.warn('scanne_include deprecated')
    return files, {}, filter(scan_include(fill(scanner), scanner.path, files=files, stack=[], include=None))







