#-*- coding: utf-8 -*-

class PPError(Exception):
    '''Super class of error for preprocessor'''
    pass

class ParserError(PPError):
    '''Exception class for configuration parsing'''
    pass

class ProxyError(PPError):
    '''Exception class for driver'''
    pass

class EventError(PPError):
    '''Exception class for events'''
    pass

class ProviderError(PPError):
    '''Error class for provider'''
    pass

class BridgeError(PPError):
    '''Error class for bridge'''
    pass