# -*- coding: utf-8 -*-

from . import IPlugin


class LoggerPlugin(IPlugin):
    '''Plug in to log data'''

    def configure(self, desc, data):
        '''Store the description of the plug in'''
        self.desc = desc

    def notify(self, *args):
        '''Log on notification'''
        print('%s: %s' % (self.desc, ', '.join([repr(arg) for arg in args])))
